UseTemplate.EMISSIVE_CODE:
(L:XMLVAR_RMP_#SIDE#_On, bool) if{
	(L:#STATE_VAR_NAME#, bool) #OPTIONAL_ACTIVE_IF# and
}
els{ 0 }
(L:XMLVAR_SWITCH_OVHD_INTLT_ANNLT_Position) 0 == or

(L:XMLVAR_RMP_#SIDE#_On, bool) (L:#GROUP_STATE_VAR_NAME#) ! and if{
	(L:#CURRENT_BUTTON_VAR_NAME#) #GROUP_INDEX# == #OPTIONAL_ACTIVE_IF# and
}
els{
	0
}
(L:XMLVAR_SWITCH_OVHD_INTLT_ANNLT_Position) 0 == or

UseTemplate.OPTIONAL_ACTIVE_IF:
(* AM is only available when HF1/HF2 are active *)
(L:XMLVAR_RMP_#SIDE#_CURRENT_BUTTON_IN_GROUP_MODE) 0 == (L:XMLVAR_RMP_#SIDE#_GROUP_FREQ_IS_DISABLED) ! and if{
	(L:XMLVAR_RMP_#SIDE#_CURRENT_BUTTON_IN_GROUP_FREQ) 4 == (L:XMLVAR_RMP_#SIDE#_CURRENT_BUTTON_IN_GROUP_FREQ) 5 == or
}

UseTemplate.EMISSIVE_CODE:
(* Both RMPs must be on *)
(L:XMLVAR_RMP_L_On, bool) (L:XMLVAR_RMP_R_On, bool) and if{
	(* Both frequency groups must be active *)
	(L:XMLVAR_RMP_L_GROUP_FREQ_IS_DISABLED) ! (L:XMLVAR_RMP_R_GROUP_FREQ_IS_DISABLED) ! and if{
		(* The currently selected frequency must be valid *)
		(L:XMLVAR_RMP_#SIDE#_CURRENT_BUTTON_IN_GROUP_FREQ) 0 > if{
			(* Both RMPs must be in the default mode *)
			(L:XMLVAR_RMP_L_CURRENT_BUTTON_IN_GROUP_MODE) 0 == (L:XMLVAR_RMP_R_CURRENT_BUTTON_IN_GROUP_MODE) 0 == and if{
				(* SEL will now be highlighted when both RMPs have the same frequency set *)
				(L:XMLVAR_RMP_L_CURRENT_BUTTON_IN_GROUP_FREQ) (L:XMLVAR_RMP_R_CURRENT_BUTTON_IN_GROUP_FREQ) ==
			}
			els{ 0 }
		}
		els{ 0 }
	}
	els{ 0 }
}
els{ 0 }

UseTemplate.OPTIONAL_ACTIVE_IF:
(* BFO is only available when ADF is active *)
(L:XMLVAR_RMP_#SIDE#_GROUP_MODE_IS_DISABLED) ! if{
	(L:XMLVAR_RMP_#SIDE#_CURRENT_BUTTON_IN_GROUP_MODE) 4 ==
}
